import React, {useState, useEffect} from 'react';
import '../../App.css';
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'
import CardItem from '../CardItem';
import '../Cards.css';

export default function CourseHome() {

    const [name, setName] = useState('')
    const [courses, setCourses] = useState([]);
    const navigate = useNavigate()

    axios.defaults.withCredentials = true;
    useEffect(()=> {
        axios.get('http://localhost:8081/coursehome')
        .then(res => {
        if(res.data.valid){
            setName(res.data.name);
            const userId = sessionStorage.getItem('userId');
            console.log("User ID course home frontend: ", userId);
            if(userId){
                axios.get(`http://localhost:8081/userCourses/${userId}`)
                .then(res => {
                    setCourses(res.data.courses);
                })
                .catch(err => console.log(err));
            }
        }else{
            navigate('/login')
        }
        })
        .catch(err => console.log(err))
    }, [])

    return (
        <div className='course_container'>
            <div className='cards'>
            <button type='submit' className='btn bg-primary w-100 rounded-0' style={{ position: 'absolute', top: 0, right: 0 }}> Create Course</button> 
                <h2>{name}'s Courses</h2>
                <hr style={{ color: 'gray', backgroundColor: 'gray', height: 1 }} />
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>
                            {courses.map(course => (
                                <CardItem
                                    key={course.course_id}
                                    src={require('/Users/alyssaingerson/Documents/GitHub/TheLearningLab/my-app/frontend/src/classroom.jpg')}
                                    text={course.courseName}
                                    path={`/course/${course.course_id}`}
                                />
                            ))}
                        </ul>
                    </div>
                </div>    
            </div>
        </div>

    )

}
