import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Navbar.css';

function Navbar() {
  const navigate = useNavigate();

  const handleLogout = async () =>{
    try{
      await axios.post('http://localhost:8081/logout');
      sessionStorage.clear(); //clear session storage
      navigate('/login'); //once logged out, redirect to login page
    }catch(err){
      console.error('Unsuccessful logout:', err);
    }
  }

  const [click, setClick] = useState(false);
  const [buttonVisible, setButtonVisible] = useState(false);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const [buttonText, setButtonText] = useState('SIGN UP');
  const isAuthenticated = sessionStorage.getItem('userType');
  //console.log(isAuthenticated);

  useEffect(() => {
    console.log('isAuthenticated:', isAuthenticated);
    setButtonText(isAuthenticated ? 'LOG OUT' : 'SIGN UP');
    setButtonVisible(!!isAuthenticated); //display button only if the user is logged in
  }, [isAuthenticated]);

  useEffect(() => {
    setButtonVisible(!!isAuthenticated);
  }, [click, isAuthenticated]);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButtonVisible(true);
    } else {
      setButtonVisible(!!isAuthenticated);
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener('resize', showButton);


  return (
    <>
      <nav className='navbar'>
        <div className='navbar-container'>
          <Link to={isAuthenticated ? (isAuthenticated === 'student' ? '/studenthome' : '/teacherhome') : '/login'} className='navbar-logo' onClick={closeMobileMenu}>
            The Learning Lab
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Courses
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/services'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Students
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/about'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                About
              </Link>
            </li>

            <li>
            <Link
              to={isAuthenticated ? (isAuthenticated === 'student' ? '/studenthome' : '/teacherhome') : '/login'}
              className='nav-links-mobile'
              onClick={closeMobileMenu}
            >
              {isAuthenticated ? 'LOG OUT' : 'SIGN UP'}
            </Link>
          </li>
        </ul>
        </div>
      </nav>
      {buttonVisible && (
          <Button buttonStyle='btn--outline' buttonText={buttonText} onClick={handleLogout}/>
      )}
    </>
  );
}

export default Navbar;
