import React, {useState, useEffect, useRef} from 'react';
import './Slideshow.css';

const images = ['/Users/alyssaingerson/Documents/GitHub/TheLearningLab/my-app/frontend/src/bloxorzpic..jpg', 
                '/Users/alyssaingerson/Documents/GitHub/TheLearningLab/my-app/frontend/src/flappybirdpic.jpg', 
                '/Users/alyssaingerson/Documents/GitHub/TheLearningLab/my-app/frontend/src/triviapic2.jpg'];
const delay = 5000;

function Slideshow(){
    const [index, setIndex] = useState(0);
    const timeoutRef = useRef(null);

    function resetTimeout(){
        if(timeoutRef.current){
            clearTimeout(timeoutRef.current);
        }
    }
    useEffect(() => {
        resetTimeout();
        timeoutRef.current = setTimeout(
            () =>
                setIndex((prevIndex) =>
                    prevIndex === images.length - 1 ? 0 : prevIndex + 1
                ),
            delay
        );

        return () => {
            resetTimeout();
        };
    }, [index]);

    return(
        <div className="slideshow">
            <div className="slideshowSlider"
            style={{transform: `translate3d(${-index * 100}%, 0, 0)`}}
        >
                {images.map((backgroundColor, index) => (
                    <img 
                        className="slide" 
                        key={index} 
                        style={{backgroundColor}}
                    />
                ))}  
            </div>

            <div className="slideshowDots">
                {images.map((_, idx) => (
                    <div 
                        key={idx} 
                        className={`slideshowDot${index === idx ? " active" : " "}`}
                        onClick={() => {
                            setIndex(idx);
                        }}
                    ></div>
                ))}
            </div>
        </div>
    )
}

export default Slideshow;
