import axios from 'axios';
import React, {useEffect, useState} from 'react';
import {Unity, useUnityContext} from 'react-unity-webgl';
import { useParams } from 'react-router-dom';
import './Game.css';

function Game() {
    const unityContext = useUnityContext({
        loaderUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated4/Build/QuizGameProjectBuildUpdated4.loader.js`,
        dataUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated4/Build/QuizGameProjectBuildUpdated4.data`,
        frameworkUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated4/Build/QuizGameProjectBuildUpdated4.framework.js`,
        codeUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated4/Build/QuizGameProjectBuildUpdated4.wasm`,
    });

    console.log('Unity Context 1:', unityContext);
    const [score, setScore] = useState(0);
    const [gameFinished, setGameFinished] = useState(false);

    const {gameId} = useParams();

    const handleGameCompleted = (isFinished) => {
        setGameFinished(isFinished);
    }

    useEffect(() => {
        if(gameFinished){
            axios.post('http://localhost:8081/updateScore', {
                gameId: gameId,
                score: score,
            })
            .then(res => {
                console.log(res.data);
                alert("Score updated successfully!");
            })
            .catch(err => {
                console.error('Error updating score:', err);
                alert("An unexpected error occurred.");
            });
        }
    }, [score, gameId]);


    console.log('Score:', score);
    console.log('Game Finished: ', gameFinished);

    return (
        <div className='game-container'>
            <div>Game Score: {score}</div>
            <Unity unityProvider={unityContext.unityProvider} style={{ width: '70%', height: '70%' }} onGameCompleted={handleGameCompleted}/>
        </div>
    );
}

export default Game;
