import React, {useEffect, useState} from 'react';
import {Unity, useUnityContext} from 'react-unity-webgl';
import './Game.css';

function Game() {
    const {unityContext} = useUnityContext({
        loaderUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuild2/Build/QuizGameProjectBuild2.loader.js`,
        dataUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuild2/Build/QuizGameProjectBuild2.data`,
        frameworkUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuild2/Build/QuizGameProjectBuild2.framework.js`,
        codeUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuild2/Build/QuizGameProjectBuild2.wasm`,
    });

    console.log('Unity Context 1:', unityContext);
    const [score, setScore] = useState(0);

    useEffect(() => {
        console.log('Unity Context:', unityContext);
        if(unityContext) {
            const handleScoreUpdated = (newScore) => {
                setScore(newScore);
            }

            unityContext.on('ScoreUpdated', handleScoreUpdated);

            return () => {
                unityContext.removeListener('ScoreUpdated', handleScoreUpdated);
            }
        }
    }, [unityContext]);

    console.log('Score:', score);

    return (
        <div className='game-container'>
            <div>Game Score: {score}</div>
            <Unity unityContext={unityContext} style={{ width: '70%', height: '70%' }}/>
        </div>
    );
}

export default Game;
