import React, {useEffect, useState} from 'react';
import {Unity, useUnityContext} from 'react-unity-webgl';
import './Game.css';

function Game() {
    const unityContext = useUnityContext({
        loaderUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated/Build/QuizGameProjectBuildUpdated.loader.js`,
        dataUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated/Build/QuizGameProjectBuildUpdated.data`,
        frameworkUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated/Build/QuizGameProjectBuildUpdated.framework.js`,
        codeUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated/Build/QuizGameProjectBuildUpdated.wasm`,
    });

    console.log('Unity Context 1:', unityContext);
    const [score, setScore] = useState(0);

    /*useEffect(() => {
        console.log('Unity Context:', unityContext);
        unityContext.send('GameManager', 'DisplayResolutionScreenListener');
        unityContext.on('unity:/DisplayResolutionScreen', (type, highScore) => {
            if(type === 'Finish'){
                setScore(highScore);
            }
        })
        /*if(unityContext && unityContext.unityConfig) {
            const handleScoreUpdated = (newScore) => {
                setScore(newScore);
            }

            unityContext.unityConfig.addEvent('ScoreUpdated', handleScoreUpdated);

            return () => {
                unityContext.unityConfig.removeEvent('ScoreUpdated', handleScoreUpdated);
            }
        }*/

    //}, [unityContext]);

    console.log('Score:', score);

    return (
        <div className='game-container'>
            <div>Game Score: {score}</div>
            <Unity unityProvider={unityContext.unityProvider} style={{ width: '70%', height: '70%' }}/>
        </div>
    );
}

export default Game;
