import React, {useState, useEffect} from 'react';
import '../../App.css';
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'
import CardItem from '../CardItem';
import '../Cards.css';

export default function CourseHome() {

    const [name, setName] = useState('')
    const [courses, setCourses] = useState([]);
    const navigate = useNavigate()

    axios.defaults.withCredentials = true;
    useEffect(()=> {
        axios.get('http://localhost:8081/coursehome')
        .then(res => {
        if(res.data.valid){
            setName(res.data.name);
            const userId = sessionStorage.getItem('userId');
            if(userId){
                axios.get(`http://localhost:8081/userCourses/${userId}`)
                .then(res => {
                    setCourses(res.data.courses);
                })
                .catch(err => console.log(err));
            }
        }else{
            navigate('/login')
        }
        })
        .catch(err => console.log(err))
    }, [])

    return (
        <div className='course-home-container'>
            <h2>{name}'s Courses</h2>
            <div className= 'cards'>
                <div className='cards__container'>
                    <div className='cards__wrapper'>
                        <ul className='cards__items'>
                            {courses.map(course => (
                                <CardItem
                                    key={course.id}
                                    src={require('/Users/alyssaingerson/Documents/GitHub/TheLearningLab/my-app/frontend/src/classroom.jpg')}
                                    text={course.name}
                                    path={`/course/${course.id}`}
                                />
                            ))}
                        </ul>
                    </div>
                </div>    
            </div>
        </div>

    )

}
