import React, {useState, useEffect} from "react";
import { useNavigate, useParams } from 'react-router-dom'
import axios from 'axios'
import CardItem from '../CardItem';
import '../../App.css';
import '../Cards.css';

function CoursePage(){
    const params = useParams();
    const [name, setName] = useState('');
    const [courses, setCourses] = useState([]);
    const navigate = useNavigate()
    const {courseId} = params;
    const [course, setCourse] = useState(null);

    axios.defaults.withCredentials = true;
    useEffect(()=> {
        axios.get('http://localhost:8081/coursehome')
        .then(res => {
        if(res.data.valid){
            setName(res.data.name);
            const userId = sessionStorage.getItem('userId');
            console.log("User ID course home frontend : ", userId);
            if(userId){
                axios.get(`http://localhost:8081/userCourses/${userId}`)
                .then(res => {
                    setCourses(res.data.courses);
                    console.log("Courses: ", res.data.courses);
                })
                .catch(err => console.log(err));
            }
        }else{
            navigate('/login')
        }
        })
        .catch(err => console.log(err))
    }, [])

    useEffect(() => {
        axios.get(`http://localhost:8081/course/${courseId}`)
        .then(res => {
            console.log("Course data: ", res.data);
            setCourse(res.data.course);
        })
        .catch(err => console.log(err));
    }, [courseId]); 

   if(!course){
    return <div>Loading...</div>;
   }

    return (
    <div className="course-page-container">
        <h1>{course.courseName}</h1>
        <h2>{course.courseDescription}</h2>
        <div className="student-entry-container">
            <div className='mb-3'>                    
                <label htmlFor="email"><strong>Student email: &nbsp;</strong></label>
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                    
                <input type="email" placeholder='Enter Email' name='email' style={{maxWidth: '100%'}} style={{maxWidth: '100%'}} className='rounded-0'/>                    
                <br/>
                <button type='submit' className='btn bg-success w-100 rounded-0'> Sign up</button>
            </div>
        </div>
    </div>
    )
}

export default CoursePage;
