import React, {useEffect, useState} from 'react';
import {Unity, useUnityContext} from 'react-unity-webgl';
import './Game.css';

function Game() {
    const unityContext = useUnityContext({
        loaderUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated2/Build/QuizGameProjectBuildUpdated2.loader.js`,
        dataUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated2/Build/QuizGameProjectBuildUpdated.data`,
        frameworkUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated2/Build/QuizGameProjectBuildUpdated2.framework.js`,
        codeUrl: `${process.env.PUBLIC_URL}/unity_builds/QuizGameProjectBuildUpdated2/Build/QuizGameProjectBuildUpdated2.wasm`,
    });

    console.log('Unity Context 1:', unityContext);
    const [score, setScore] = useState(0);

    const {gameId} = useParams();

    useEffect(() => {
        axios.post('http://localhost:8081/updateScore', {
            gameId: gameId,
            score: score,
        })
        .then(res => {
            console.log(res.data);

            alert("Score updated successfully!");
        })
        .catch(err => {
            console.error('Error updating score:', err);
            alert("An unexpected error occurred.");
        });
    }, [score, gameId]);



    console.log('Score:', score);

    return (
        <div className='game-container'>
            <div>Game Score: {score}</div>
            <Unity unityProvider={unityContext.unityProvider} style={{ width: '70%', height: '70%' }}/>
        </div>
    );
}

export default Game;
