import React, {useState, useEffect} from "react";
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios'
import CardItem from '../CardItem';
import '../Cards.css';

function CoursePage({match}){
    const [name, setName] = useState('')
    const [courses, setCourses] = useState([]);
    const navigate = useNavigate()
    const courseId = match.params.courseId;
    const [course, setCourse] = useState(null);

    axios.defaults.withCredentials = true;
    useEffect(()=> {
        axios.get('http://localhost:8081/coursehome')
        .then(res => {
        if(res.data.valid){
            setName(res.data.name);
            const userId = sessionStorage.getItem('userId');
            console.log("User ID course home frontend: ", userId);
            if(userId){
                axios.get(`http://localhost:8081/userCourses/${userId}`)
                .then(res => {
                    setCourses(res.data.courses);
                })
                .catch(err => console.log(err));
            }
        }else{
            navigate('/login')
        }
        })
        .catch(err => console.log(err))
    }, [])

    useEffect(() => {
        axios.get(`http://localhost:8081/course/${courseId}`)
        .then(res => {
            setCourse(res.data);
        })
        .catch(err => console.log(err));
    }, [courseId]); 



    return (
    <div>
        <h1>{course.courseName}</h1>
        <h2>{course.courseDescription}</h2>
    </div>
    )
}

export default CoursePage;
